mapper.select:
SELECT * FROM t_pl_medicalwastes
    WHERE DATE_FORMAT(dateTime, '%Y-%m-%d') >=  DATE_FORMAT(#{0}, '%Y-%m-%d')
    AND DATE_FORMAT(dateTime, '%Y-%m-%d') <=  DATE_FORMAT(#{1}, '%Y-%m-%d');

SELECT * FROM t_pl_medicalwastes
   WHERE DATE_FORMAT(dateTime, '%Y-%m-%d') >=  DATE_FORMAT(#{0}, '%Y-%m-%d')
   AND DATE_FORMAT(dateTime, '%Y-%m-%d') <=  DATE_FORMAT(#{1}, '%Y-%m-%d') and equipment=#{2};
