mapper.update:
update t_pr_pretreatment
set
nowTime = NOW(),wasteLiquidProportion =#{wasteLiquidProportion},bulkProportion =#{bulkProportion},
crushingProportion =#{crushingProportion},distillationProportion=#{distillationProportion},
suspensionProportion=#{suspensionProportion},sludgeProportion=#{sludgeProportion}
where id = #{id};


        update t_pr_pretreatmentitem
        set
        processWay = #{pretreatmentItem.wastes.processWay},handleCategory =
        #{pretreatmentItem.wastes.handleCategory}
        where itemId = #{pretreatmentItem.itemId};

mapper.insert:
insert into t_pr_pretreatment (id,creationDate,state,remarks,weightTotal,calorificTotal,ashPercentageTotal, wetPercentageTotal,volatileNumberTotal,chlorinePercentageTotal,sulfurPercentageTotal, phTotal,phosphorusPercentageTotal,fluorinePercentageTotal,distillationProportion, wasteLiquidProportion,sludgeProportion,bulkProportion,crushingProportion,suspensionProportion,nowTime) values (#{id},NOW(),'NewBuild',#{remarks},#{weightTotal},#{calorificTotal},#{ashPercentageTotal}, #{wetPercentageTotal},#{volatileNumberTotal},#{chlorinePercentageTotal},#{sulfurPercentageTotal}, #{phTotal},#{phosphorusPercentageTotal},#{fluorinePercentageTotal},#{distillationProportion}, #{wasteLiquidProportion},#{sludgeProportion},#{bulkProportion},#{crushingProportion},#{suspensionProportion},NOW());
insert into t_pr_pretreatmentitem (itemId,serialNumber,pretreatmentId,outboundOrderId,produceCompanyName,requirements,proportion,weight,volatileNumber, calorific,ashPercentage,wetPercentage,chlorinePercentage,sulfurPercentage,ph,phosphorusPercentage, fluorinePercentage,remarks,processWay,handleCategory,wastesName) values (#{pretreatmentItem.itemId},#{pretreatmentItem.serialNumber},#{id},#{pretreatmentItem.outboundOrderId},#{pretreatmentItem.produceCompanyName},#{pretreatmentItem.requirements}, #{pretreatmentItem.proportion},#{pretreatmentItem.wastes.weight},#{pretreatmentItem.wastes.volatileNumber}, #{pretreatmentItem.wastes.calorific},#{pretreatmentItem.wastes.ashPercentage},#{pretreatmentItem.wastes.wetPercentage}, #{pretreatmentItem.wastes.chlorinePercentage},#{pretreatmentItem.wastes.sulfurPercentage},#{pretreatmentItem.wastes.ph}, #{pretreatmentItem.wastes.phosphorusPercentage},#{pretreatmentItem.wastes.fluorinePercentage},#{pretreatmentItem.wastes.remarks}, #{pretreatmentItem.wastes.processWay},#{pretreatmentItem.wastes.handleCategory},#{pretreatmentItem.wastes.name});
update t_pl_outboundorder set checkState = "Processed" where outboundOrderId = #{outBoundOrderId};
