mapper.insert:
INSERT INTO t_quotation (quotationId, name, version, clientId, contactName, phone, location, startDate, endDate, isContainTax, isContainFreight, totalPrice, totalPriceTax, totalAmount, checkState, isStamp, nowTime)
VALUES (#{quotationId}, #{name}, #{version}, #{client.clientId}, #{client.contactName}, #{client.phone}, #{client.location}, #{startDate}, #{endDate}, #{isContainTax}, #{isContainFreight}, #{totalPrice}, #{totalPriceTax}, #{totalAmount}, #{checkState}, #{isStamp}, NOW());


        INSERT INTO r_quotationandwastes (quotationId, wastesId) VALUES (#{id}, #{wastes.id});
        INSERT INTO t_wastes (id, name, formType, wastesId, contractAmount, unitPriceTax, unitPrice, taxRate, ph, ashPercentage, wetPercentage, calorific, halogenPercentage, sulfurPercentage, flashPoint, nowTime)
        VALUES (#{wastes.id}, #{wastes.name}, #{wastes.formType}, #{wastes.wastesId}, #{wastes.contractAmount}, #{wastes.unitPriceTax}, #{wastes.unitPrice}, #{wastes.taxRate}, #{wastes.ph},
        #{wastes.ashPercentage}, #{wastes.wetPercentage}, #{wastes.calorific}, #{wastes.halogenPercentage}, #{wastes.sulfurPercentage}, #{wastes.flashPoint}, NOW());
        UPDATE t_wastes SET contractAmount=#{wastes.contractAmount}, unitPriceTax=#{wastes.unitPriceTax}, unitPrice=#{wastes.unitPrice}, taxRate=#{wastes.taxRate} WHERE wastesId=#{wastes.wastesId} AND clientId=#{client.clientId};

mapper.update:
UPDATE t_quotation SET name=#{name}, version=#{version}, clientId=#{client.clientId}, contactName=#{client.contactName}, phone=#{client.phone}, location=#{client.location}, startDate=#{startDate}, endDate=#{endDate},
isContainTax=#{isContainTax}, isContainFreight=#{isContainFreight}, isStamp=#{isStamp}, totalPrice=#{totalPrice}, totalPriceTax=#{totalPriceTax}, totalAmount=#{totalAmount}, nowTime=NOW() WHERE id=#{id};


        UPDATE t_wastes SET name=#{wastes.name}, formType=#{wastes.formType}, wastesId=#{wastes.wastesId}, contractAmount=#{wastes.contractAmount}, unitPriceTax=#{wastes.unitPriceTax},
        unitPrice=#{wastes.unitPrice}, taxRate=#{wastes.taxRate}, ph=#{wastes.ph}, ashPercentage=#{wastes.ashPercentage}, wetPercentage=#{wastes.wetPercentage}, calorific=#{wastes.calorific},
        halogenPercentage=#{wastes.halogenPercentage}, sulfurPercentage=#{wastes.sulfurPercentage}, flashPoint=#{wastes.flashPoint}, nowTime=NOW() WHERE id=#{wastes.id};
        UPDATE t_wastes SET contractAmount=#{wastes.contractAmount}, unitPriceTax=#{wastes.unitPriceTax}, unitPrice=#{wastes.unitPrice}, taxRate=#{wastes.taxRate} WHERE wastesId=#{wastes.wastesId} AND clientId=#{client.clientId};

mapper.insert:
INSERT INTO t_quotation (quotationId, name, version, clientId, contactName, phone, location, startDate, endDate, isContainTax, isContainFreight, isStamp, totalPriceTax, totalPrice, totalAmount, checkState, nowTime)
VALUES (#{quotationId}, #{name}, #{version}, #{client.clientId}, #{client.contactName}, #{client.phone}, #{client.location}, #{startDate}, #{endDate}, #{isContainTax}, #{isContainFreight}, #{isStamp}, #{totalPriceTax}, #{totalPrice}, #{totalAmount}, #{checkState}, NOW());


        INSERT INTO r_quotationandwastes (quotationId, wastesId) VALUES (#{id}, #{wastes.id});
        INSERT INTO t_wastes (id, name, formType, wastesId, contractAmount, unitPriceTax, unitPrice, taxRate, ph, ashPercentage, wetPercentage, calorific, halogenPercentage, sulfurPercentage, flashPoint, nowTime)
        VALUES (#{wastes.id}, #{wastes.name}, #{wastes.formType}, #{wastes.wastesId}, #{wastes.contractAmount}, #{wastes.unitPriceTax}, #{wastes.unitPrice}, #{wastes.taxRate}, #{wastes.ph},
        #{wastes.ashPercentage}, #{wastes.wetPercentage}, #{wastes.calorific}, #{wastes.halogenPercentage}, #{wastes.sulfurPercentage}, #{wastes.flashPoint}, NOW());
        UPDATE t_wastes SET contractAmount=#{wastes.contractAmount}, unitPriceTax=#{wastes.unitPriceTax}, unitPrice=#{wastes.unitPrice}, taxRate=#{wastes.taxRate} WHERE wastesId=#{wastes.wastesId} AND clientId=#{client.clientId};

mapper.select:
SELECT * FROM t_wastes WHERE id in (
  SELECT r_quotationandwastes.wastesId FROM r_quotationandwastes
  WHERE quotationId=#{id});
