mapper.select:
SELECT * FROM salesman


        and salesmanId like "%"#{keyword}"%" or name like "%"#{keyword}"%"


        and salesmanId LIKE "%"#{salesmanId}"%"


        and name LIKE "%"#{name}"%"


        and sex = #{sex}


        and age = #{age}


order by nowTime desc

    limit #{page.start}, #{page.count}

SELECT COUNT(*) FROM salesman


        and salesmanId like "%"#{keyword}"%" or name like "%"#{keyword}"%"


        and salesmanId LIKE "%"#{salesmanId}"%"


        and name LIKE "%"#{name}"%"


        and sex = #{sex}


        and age = #{age}
