mapper.update:
UPDATE t_pr_sampleinfowarehouse SET companyCode=#{companyCode },companyName=#{companyName},wastesCode =#{wastesList[0].code}, sendingPerson = #{sendingPerson},isPH = #{wastesList[0].isPH},isAsh = #{wastesList[0].isAsh}, isWater = #{wastesList[0].isWater},isHeat = #{wastesList[0].isHeat},isSulfur =#{wastesList[0].isSulfur}, isChlorine = #{wastesList[0].isChlorine},isFluorine = #{wastesList[0].isFluorine},isPhosphorus =#{wastesList[0].isPhosphorus}, isFlashPoint =#{wastesList[0].isFlashPoint},isViscosity = #{wastesList[0].isViscosity},isHotMelt=#{wastesList[0].isHotMelt},wastesFormType=#{wastesList[0].formType}, nowTime = NOW() WHERE t_pr_sampleinfowarehouse.id =#{id};
insert into t_pr_sampleinfowarehouseitem (id,transferId,sampleId,code,name,category,formType,isPH,isAsh,isWater,isHeat,isSulfur,isChlorine,isFluorine, isPhosphorus,isFlashPoint,isViscosity,isHotMelt) values (#{wastes.id},#{wastes.transferId},#{id},#{wastes.code},#{wastes.name},#{wastes.category},#{wastes.formType}, #{wastes.isPH},#{wastes.isAsh},#{wastes.isWater},#{wastes.isHeat},#{wastes.isSulfur},#{wastes.isChlorine},#{wastes.isFluorine},#{wastes.isPhosphorus}, #{wastes.isFlashPoint},#{wastes.isViscosity},#{wastes.isHotMelt});
update t_pr_sampleinfowarehouseitem set transferId=#{wastes.transferId},code=#{wastes.code},name=#{wastes.name},category=#{wastes.category}, formType=#{wastes.formType},isPH=#{wastes.isPH},isAsh=#{wastes.isAsh},isWater=#{wastes.isWater}, isHeat=#{wastes.isHeat},isSulfur=#{wastes.isSulfur},isChlorine=#{wastes.isChlorine},isFluorine=#{wastes.isFluorine}, isPhosphorus=#{wastes.isPhosphorus},isFlashPoint=#{wastes.isFlashPoint},isViscosity=#{wastes.isViscosity}, isHotMelt=#{wastes.isHotMelt} where id=#{wastes.id}
