mapper.select:
select * from t_pr_secondarytest


        or id like "%"#{keyword}"%"
        or wastesName like "%"#{keyword}"%"
        or scorchingRate like "%"#{keyword}"%"
        or water like "%"#{keyword}"%"
        or remarks like "%"#{keyword}"%"
        or dateTime like "%"#{keyword}"%"


             and id like "%"#{id}"%"


        and wastesName like "%"#{wastesName}"%"


        and remarks like "%"#{remarks}"%"


        and scorchingRate = #{scorchingRate}


        and water = #{water}


        and dateTime  >=   #{dateTimeStart}


        and dateTime  <=   #{dateTimeEnd}



    limit #{page.start}, #{page.count}

select count(*) from t_pr_secondarytest
or id like "%"#{keyword}"%" or wastesName like "%"#{keyword}"%" or scorchingRate like "%"#{keyword}"%" or water like "%"#{keyword}"%" or remarks like "%"#{keyword}"%" or dateTime like "%"#{keyword}"%"
