mapper.update:
update t_pr_sampleinfoanalysis set sampleId=#{sampleId}, transferDraftId=#{transferDraftId},
produceCompanyId=#{produceCompany.clientId}, wastesName=#{wastesName}, wastesCode=#{wastesCode},
wastesCategory=#{wastesCategory} ,handleCategory=#{handleCategory}, formType=#{formType},
sender=#{sender}, signer=#{signer}, signDate=#{signDate}, remark=#{remark} where id=#{id};

    update t_pr_sampleinfoanalysis set PH=#{PH} where id=#{id} and PH != '-9999';


    update t_pr_sampleinfoanalysis set ash=#{ash} where id=#{id} and ash != '-9999';


    update t_pr_sampleinfoanalysis set water=#{water} where id=#{id} and water != '-9999';


    update t_pr_sampleinfoanalysis set heat=#{heat} where id=#{id} and heat != '-9999';


    update t_pr_sampleinfoanalysis set sulfur=#{sulfur} where id=#{id} and sulfur != '-9999';


    update t_pr_sampleinfoanalysis set fluorine=#{fluorine} where id=#{id} and fluorine != '-9999';


    update t_pr_sampleinfoanalysis set chlorine=#{chlorine} where id=#{id} and chlorine != '-9999';


    update t_pr_sampleinfoanalysis set phosphorus=#{phosphorus} where id=#{id} and phosphorus != '-9999';


    update t_pr_sampleinfoanalysis set flashPoint=#{flashPoint} where id=#{id} and flashPoint != '-9999';


    update t_pr_sampleinfoanalysis set viscosity=#{viscosity} where id=#{id} and viscosity != '-9999';


    update t_pr_sampleinfoanalysis set hotMelt=#{hotMelt} where id=#{id} and hotMelt != '-9999';
